import { Component, Inject } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UtilisateurService } from '../../Services/utilisateur.service';
import { UtilisateurApplicationService } from '../../Services/utilisateurapplication.service';
import { UtilisateurApplication } from '../../Models/utilisateurapplication.model';
import { ApplicationService } from '../../Services/application.service';


@Component({
  selector: 'app-Utilisateur',
  templateUrl: './Utilisateur.component.html'
})
export class UtilisateurComponent {
  Utilisateurs: any[] = [];
  rech: string = "";
  IdApp: number = 0;
  applications: any[] = [];
  UtilisateurApp: UtilisateurApplication = new UtilisateurApplication(); 
  UtilisateurApps: UtilisateurApplication[] = [];

  constructor(private UAService: UtilisateurApplicationService, private ApplicationService: ApplicationService
    ,private UtilisateurService: UtilisateurService, private router: Router, private route: ActivatedRoute){}


  ngOnInit(): void {
    this.loadUtilisateur();
  }



  loadUtilisateur() {
    this.UtilisateurService.getUtilisateurs().subscribe((data) => {
      this.Utilisateurs = data;   
    }, (error) => { console.error('Erreur lors de la récupération des Utilisateur : ', error); });
  }
  

  Recherche(){
    if (this.rech == "") this.loadUtilisateur();
    else {
      this.UtilisateurService.getUtilisateurs().subscribe((data) => {
        this.Utilisateurs = data;
        this.Utilisateurs = this.Utilisateurs.filter(user => {
          return user.userName.toLowerCase().includes(this.rech.toLowerCase()) ||
                 user.email.toLowerCase().includes(this.rech.toLowerCase()) ||
                 user.id.toLowerCase().includes(this.rech.toLowerCase());
        });
      });
    }
  }


  onModifierClick(id:string) { this.router.navigate(['Utilisateur/ModifierUtilisateur', id]); }


  deleteUtilisateur(id: string) {
    if (confirm('Voulez-vous vraiment supprimer ce utilisateur ?')) {
      this.UtilisateurService.deleteUtilisateur(id).subscribe(() => {
        this.UtilisateurService.getUtilisateur(id).subscribe((data) => {
          if (localStorage.getItem("username") == data.userName) {

          }
        })
        this.loadUtilisateur();
      });
    }
  }





}





<div class="background-titre"> <h1> GESTION DES UTILISATEURS </h1> </div>
<div class="centered-element">
  <div class="content">

    <div style="display:flex; justify-content:center;">
      <button class="user-button" id="user-button" [routerLink]="['/Utilisateur']" [routerLinkActive]="['user-button-active']" [routerLinkActiveOptions]="{ exact: true }">
        <i class="fa-solid fa-list"></i>  &nbsp; LISTE
      </button>
      <button class="user-button" id="user-button" [routerLink]="['/Utilisateur/Acces']" [routerLinkActive]="['user-button-active']" >
        <i class="fa-solid fa-user-lock"></i> &nbsp; ACCÈS
      </button>
      <button class="user-button" id="user-button" [routerLink]="['/Utilisateur/Activite']" [routerLinkActive]="['user-button-active']" >
        <i class="fa-solid fa-clock-rotate-left"></i>  &nbsp; ACTIVITÉ
      </button>
    </div>


    <div class="search-bar-and-add">
      <button class="add-button" [routerLink]="['/Utilisateur/AjouterUtilisateur']">
        <i class="fas fa-plus" style="color: #1ddd92;"></i>  &nbsp;  AJOUTER
      </button>
      <input [(ngModel)]="rech" (input)="Recherche()" class="search-input" type="text" placeholder="Rechercher ( guid ,username ,email )...">
      <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
    </div>
    <br />

    <table aria-labelledby="tableLabel" *ngIf="Utilisateurs">
      <thead>
        <tr>
          <th>GUID</th>
          <th>UTILISATEUR</th>
          <th>EMAIL</th>
          <th>NOM</th>
          <th>PRENOM</th>
          <th>STATUS</th>
          <th>ACTION</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let Utilisateur of Utilisateurs">
          <td>{{ Utilisateur.id }}</td>
          <td>{{ Utilisateur.userName }}</td>
          <td>{{ Utilisateur.email }}</td>
          <td>{{ Utilisateur.nom }}</td>
          <td>{{ Utilisateur.prenom }}</td>
          <td>
            <span style="cursor:not-allowed" class="centered-element" appCellStyle="{{ Utilisateur.status === false ? '✖' : '✔' }}">
              {{ Utilisateur.status === false ? '✖' : '✔' }}
            </span>
          </td>
          <td>
            &nbsp; <i (click)="onModifierClick(Utilisateur.id)" [routerLink]="['/Utilisateur/ModifierUtilisateur']" class="far fa-edit" style="color: #e6c347;"></i>
            &nbsp; <i (click)="deleteUtilisateur(Utilisateur.id)" class="far fa-trash-alt" style="color: #de0707;"></i>&nbsp;
          </td>
        </tr>
      </tbody>
    </table>


  </div>
</div>
