import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { RoleService } from '../../../Services/role.service';
import { ApplicationService } from '../../../Services/application.service';
import { Role } from '../../../Models/role.model';
import { Application } from '../../../Models/application.model';

@Component({
  selector: 'app-ModifierRole',
  templateUrl: './ModifierRole.component.html'
})

export class ModifierRoleComponent implements OnInit {
  public Role: Role = new Role();
  public application: Application = new Application();
  IdApp: number = 0;

  constructor(private RoleService: RoleService, private ApplicationService: ApplicationService, private router: Router, private route: ActivatedRoute){}

  ngOnInit(): void {
    const idParam = this.route.snapshot.paramMap.get('id');
    if (idParam) {
      const id = +idParam;
      if (!isNaN(id)) {
        this.RoleService.getRole(id).subscribe((data) => {
          if (data) { this.Role = data; console.log(data); }
          this.ApplicationService.getApplication(this.Role.idApplication).subscribe((data) => { if (data) { this.application = data; console.log(data); } });
        });
      }
      else { console.error('La valeur du paramètre "id" n\'est pas un nombre valide.'); }
    }
    else { console.error('Le paramètre "id" est manquant dans l\'URL.'); }
  }


  onSubmit() {
    console.log(this.Role);
    this.RoleService.updateRole(this.Role.idApplication, this.Role).subscribe((response) => {
      if (response) { this.router.navigate(['/Role']); }
    });
  }






}


<div class="background-titre"> <h1> GESTION DES RÔLES </h1> </div>


<div class="ajouter-content">

  <div class="ajouter-entete">
    <div class="modifier-return" [routerLink]="['/Role']"> <i class="fa-solid fa-angles-left"></i> RETURN </div>
    <div class="modifier-titre"> MODIFICAITON DU RÔLE </div>
  </div>



  <form (ngSubmit)="onSubmit()" class="yellow-border">
    <div class="form-content">

      <div class="form-content-rows">
        <label for="nomApp">APPLICATION :</label>
        <input class="yellow-border" type="text" id="nomApp" name="nomApp" [(ngModel)]="application.nom" style="cursor:not-allowed" readonly>
      </div>

      <div class="form-content-rows">
        <label for="nom">RÔLE :</label>
        <input class="yellow-border" type="text" id="nom" name="nom" [(ngModel)]="Role.nom" required>
      </div>

      <div class="form-content-rows">
        <label for="nom">NIVEAU :</label>
        <input class="yellow-border" type="number" id="niveau" name="niveau" [(ngModel)]="Role.niveau" required>
      </div>

      <div class="form-content-rows">
        <label for="description">DESCRIPTION :</label>
        <input class="yellow-border" type="text" id="description" name="description" [(ngModel)]="Role.description" required>
      </div>


      <div class="form-content-rows">
        <label class="no-select"></label> <div> <button type="submit" class="valider">VALIDER</button> </div>
      </div>


    </div>
  </form>


</div>
