import { HttpErrorResponse } from '@angular/common/http';
import { IResetPassword } from '../../auth/resetPassword.interface';
import { UtilisateurService } from './../../Services/utilisateur.service';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, ValidatorFn, AbstractControl } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PasswordConfirmationValidatorService } from './../../auth/password-confirmation-validator.service';


@Component({
  selector: 'app-reset-password',
  templateUrl: './reset-password.component.html'
})

export class ResetPasswordComponent implements OnInit {
  resetPasswordForm!: FormGroup;
  showSuccess!: boolean;
  showError!: boolean;
  errorMessage!: string;
  private token!: string;
  private email!: string;
  constructor(private UtilisateurService: UtilisateurService, private passConfValidator: PasswordConfirmationValidatorService,
    private route: ActivatedRoute){}

  ngOnInit(): void {
    this.resetPasswordForm = new FormGroup({
      oldPassword: new FormControl('', [Validators.required]),
      password: new FormControl('', [Validators.required]),
      confirm: new FormControl('')
    });
    this.resetPasswordForm.get('confirm')!.setValidators([Validators.required,
    this.passConfValidator.validateConfirmPassword(this.resetPasswordForm.get('password')!)]);
    this.token = this.route.snapshot.queryParams['token'];
    this.email = this.route.snapshot.queryParams['email'];
  }


  validateControl = (controlName: string) => {
     return this.resetPasswordForm.get(controlName)!.invalid && this.resetPasswordForm.get(controlName)!.touched
  }

  hasError = (controlName: string, errorName: string) => {
     return this.resetPasswordForm.get(controlName)!.hasError(errorName)
  }

  resetPassword = (resetPasswordFormValue: any) => {
    this.showError = this.showSuccess = false;
    const resetPass = { ...resetPasswordFormValue };
    const resetPassDto: IResetPassword = {
       oldPassword: resetPass.oldPassword,
       password: resetPass.password,
       confirmPassword: resetPass.confirm,
       token: this.token,
       email: this.email
    }
    this.UtilisateurService.checkPassword(this.email, resetPass.oldPassword).subscribe({
      next: (res: Response) => {
        this.UtilisateurService.resetPassword(resetPassDto).subscribe({
          next: (res: Response) => {
            this.showSuccess = true;
            const resetPasswordBtn = document.getElementById('resetPasswordBtn') as HTMLButtonElement;
            resetPasswordBtn.disabled = true;
            resetPasswordBtn.style.backgroundColor = 'lightgray';
            resetPasswordBtn.style.borderColor = '#111';
            resetPasswordBtn.style.color = '#100564';
            resetPasswordBtn.style.cursor = 'not-allowed';
          },
          error: (err: HttpErrorResponse) => {
            if (resetPass.password) {
              this.showError = true;
              this.errorMessage = err.message;
            }
            else { 
            this.showError = true;
            this.errorMessage = "Le lien de réinitialisation du mot de passe est expiré. Veuillez effectuer une nouvelle demande de réinitialisation si nécessaire.";
            }
          }
        });
      },
      error: () => {
        this.showError = true;
        this.errorMessage = "Erreur de verification ,veuillez saisir l'ancien mot de passe correctement !"
      }
    });    
  }


  
}
<div class="AccueilContent" style="display:flex; justify-content:center; align-items:center;">

  <img src="../../assets/Images/PC-AccueilPrincipale.png" style="width:350px; height:350px" />

  <div style="display:flex; flex-direction:column; justify-content:center; align-items:center; margin:0px 0px 0px -20px;">
    <img src="../../assets/Images/WBConcept-AccueilPrincipale.png" style="max-width:325px;" />
    <div style="max-width:600px;" *ngIf="showError" class="alert alert-danger centered-element" role="alert"> <span [innerHTML]="errorMessage"></span>  </div>
    <div style="max-width:600px;" *ngIf="showSuccess" class="alert alert-success centered-element" role="alert">
      Votre mot de passe a été réinitialisé. Veuillez &nbsp; <a [routerLink]="['/Authentification']"> cliquer ici pour vous connecter. </a>
    </div>

    <form class="register" [formGroup]="resetPasswordForm" autocomplete="off" novalidate (ngSubmit)="resetPassword(resetPasswordForm.value)">
      <em *ngIf="validateControl('oldPassword') && hasError('oldPassword', 'required')">Saisir l'ancien mot de passe !</em>
      <input class="auth-field" type="password" id="oldPassword" placeholder="ANCIEN MOT DE PASSE" formControlName="oldPassword" />

      <em *ngIf="validateControl('password') && hasError('password', 'required')">Saisir le mot de passe !</em>
      <input class="auth-field" type="password" id="password" placeholder="MOT DE PASSE" formControlName="password" />

      <em *ngIf="validateControl('confirm') && hasError('confirm', 'required')">Saisir la confirmation de mot de passe !</em>
      <input class="auth-field" type="password" id="confirm" placeholder="CONFIRMATION MOT DE PASSE" formControlName="confirm" />
      <em *ngIf="hasError('confirm', 'mustMatch')" style="transform:translateY(-10px)">Les mots de passe doivent être identiques !</em>

      <button id="resetPasswordBtn" type="submit" class="auth-btn" [disabled]="!resetPasswordForm.valid">VALIDER</button>
      <div style="color:#C4BDF6; padding-top: 5px"> •────────────────────────• </div>
      <div class="mdp-oublié" [routerLink]="['/Authentification']">  Revenir à l'écran de connexion </div>

    </form>
  </div>
</div>
