import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ApplicationService } from '../../Services/application.service';
import { Application } from '../../Models/application.model';
import { RoleService } from '../../Services/role.service';
import { Role } from '../../Models/role.model';
import { filter, Observable } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';


@Component({
  selector: 'app-Application',
  templateUrl: './Application.component.html'
})

export class ApplicationComponent implements OnInit {
  applications: any[] = [];
  application: Application = new Application();
  rech: string = '';
  AppParDefaut: any = {
    id: 1 ,nom: "WBAuth", url: "www.wbauth.com", auth2FA: true, authGoogle: true, authFacebook: true, authLinkedIn: true,
  }
  RoleParDefaut: any = {
    nom: "Utilisateur", niveau: 1, idApplication: this.AppParDefaut.id,  application: this.AppParDefaut,
  }


  constructor(private roleService: RoleService, private applicationService: ApplicationService
     ,private router: Router, private route: ActivatedRoute){}


  ngOnInit(): void {  this.loadApplications(); }


  loadApplications() {
    this.applicationService.getApplications().subscribe((data) => {
      this.applications = data;
      const app = this.applications.filter(app => app.url.toLowerCase() === 'www.wbauth.com');
      if (app.length == 0) {
        this.applicationService.addApplication(this.AppParDefaut).subscribe(() => {
          this.roleService.addRole(this.RoleParDefaut);
        });
      }
    },
    (error) => { console.error('Erreur lors de la récupération des applications : ', error);});
  }

  Recherche(){
    if (this.rech == "") this.loadApplications();
    else  this.applicationService.Recherche(this.rech).subscribe((data) => { this.applications = data; });
  }

  onModifierClick(id: number) { this.router.navigate(['Application/ModifierApplication', id]); }


  deleteApplication(id: number) {
    this.applicationService.getApplication(id).subscribe((data) => {
      if (data.url.toLowerCase() == "www.wbauth.com") {
        alert("l'application WBAuth est insupprimable ,car c'est l'application elle-même en cours d'utilisation");
      }
      else {
        if (confirm('Voulez-vous vraiment supprimer cette application ?')) {
          this.applicationService.deleteApplication(id).subscribe(() => { this.loadApplications(); });
        }
      }
   });
  }




}







<div class="background-titre"> <h1> GESTION DES APPLICATIONS </h1> </div>
<div class="centered-element">
  <div class="content">

    <div class="search-bar-and-add">
      <button class="add-button" [routerLink]="['/Application/AjouterApplication']" [routerLinkActive]="['link-active']">
        <i class="fas fa-plus" style="color: #1ddd92;"></i>  &nbsp;  AJOUTER
      </button>
      <input class="search-input" type="text" placeholder="Rechercher..." [(ngModel)]="rech" (input)="Recherche()">
      <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
    </div>
    <br />

    <table aria-labelledby="tableLabel" *ngIf="applications">
      <thead>
        <tr>
          <th>ID</th>
          <th>NOM DE L'APPLICATION</th>
          <th>URL</th>
          <th>LOGO</th>
          <th>DESCRIPTION</th>
          <th><div style="display: flex; justify-content: center; align-items: center;">AUTHENTIFICATION 2FA</div></th>
          <th>
            <div style="display: flex; justify-content: center; align-items: center;"><img class="no-select" src="../../../../assets/Images/google-icon.png" width="30px" height="30px" /> </div>
          </th>
          <th>
            <div style="display: flex; justify-content: center; align-items: center;"><img class="no-select" src="../../../../assets/Images/facebook-icon.png" width="35px" height="35px" /> </div>
          </th>
          <th>
            <div style="display: flex; justify-content: center; align-items: center;"><img class="no-select" src="../../../../assets/Images/linkedin-icon.png" width="35px" height="35px" /> </div>
          </th>
          <th>ACTION</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let application of applications">
          <td>{{ application.id }}</td>
          <td>{{ application.nom }}</td>
          <td>{{ application.url }}</td>
          <td>{{ application.logo }}</td>
          <td>{{ application.description }}</td>
          <td><span style="display:flex; justify-content:center; align-items:center;">{{ application.auth2FA === false ? '✖' : '✔' }}</span></td>
          <td><span style="display:flex; justify-content:center; align-items:center;">{{ application.authGoogle === false ? '✖' : '✔' }}</span></td>
          <td><span style="display:flex; justify-content:center; align-items:center;">{{ application.authFacebook === false ? '✖' : '✔' }}</span></td>
          <td><span style="display:flex; justify-content:center; align-items:center;">{{ application.authLinkedIn === false ? '✖' : '✔' }}</span></td>
          <td>
            &nbsp; <i (click)="onModifierClick(application.id)" [routerLink]="['/Application/ModifierApplication']" [routerLinkActive]="['link-active']" class="far fa-edit" style="color: #e6c347;"></i>
            &nbsp; <i (click)="deleteApplication(application.id)" class="far fa-trash-alt" style="color: #de0707;"></i>&nbsp;
          </td>
        </tr>
      </tbody>
    </table>

  </div>
</div>
