import { HttpErrorResponse } from '@angular/common/http';
import { IForgotPassword } from '../../auth/forgotPassword.interface';
import { UtilisateurService } from './../../Services/utilisateur.service';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';


@Component({
  selector: 'app-ForgotPassword',
  templateUrl: './forgot-password.component.html',
})

export class ForgotPassword implements OnInit {
  forgotPasswordForm!: FormGroup;
  successMessage!: string;
  errorMessage!: string;
  showSuccess!: boolean;
  showError!: boolean;

  constructor(private UtilisateurService: UtilisateurService){}

  ngOnInit(): void {
    this.forgotPasswordForm = new FormGroup({  email: new FormControl("", [Validators.required])   })
  }


  public validateControl = (controlName: string) => {
    return this.forgotPasswordForm.get(controlName)!.invalid && this.forgotPasswordForm.get(controlName)!.touched
  }


  public hasError = (controlName: string, errorName: string) => {
    return this.forgotPasswordForm.get(controlName)!.hasError(errorName)
  }


  public forgotPassword = (forgotPasswordFormValue: any) => {
    this.showError = this.showSuccess = false;
    const forgotPass = { ...forgotPasswordFormValue };
    const forgotPassDto: IForgotPassword = {
      email: forgotPass.email,
      clientURI: 'http://localhost:4200/resetPassword'
    }
    this.UtilisateurService.forgotPassword(forgotPassDto).subscribe({
        next: (_) => {
          this.showSuccess = true;
          this.successMessage = 'Le lien a été envoyé, veuillez vérifier votre courrier électronique pour réinitialiser votre mot de passe.'
        },
        error: (err: HttpErrorResponse) => {
          this.showError = true;
          this.errorMessage = err.message;
        }
      })
  }
}

<div class="AccueilContent" style="display:flex; justify-content:center; align-items:center;">

  <img src="../../assets/Images/PC-AccueilPrincipale.png" style="width:350px; height:350px" />

  <div style="display:flex; flex-direction:column; justify-content:center; align-items:center; margin:0px 0px 0px -20px;">
    <img src="../../assets/Images/WBConcept-AccueilPrincipale.png" style="width:70%;" />

      <div *ngIf="showError" class="alert alert-danger" role="alert">  {{errorMessage}} </div>
      <div *ngIf="showSuccess" class="alert alert-success" role="alert">  {{successMessage}} </div>

      <form class="register" [formGroup]="forgotPasswordForm" autocomplete="off" novalidate (ngSubmit)="forgotPassword(forgotPasswordForm.value)">
        <em *ngIf="validateControl('email') && hasError('email', 'required')">Email is required</em>
        <input class="auth-field" type="text" placeholder="EMAIL" id="email" name="email" formControlName="email" required>

        <div style="color:#C4BDF6; margin-bottom: 20px"> •────────────────────────• </div>
        <button type="submit" class="auth-btn" [disabled]="!forgotPasswordForm.valid">VALIDER</button>
        <div style="width:6%;"></div>
        <div class="mdp-oublié" [routerLink]="['/ForgotPassword']">  Mot de passe oublié ? </div>
        <div class="auth-inscri-btn" [routerLink]="['/Inscription']"> CRÉER NOUVEAU COMPTE </div>

      </form>
  </div>
</div>
