import { HttpErrorResponse } from '@angular/common/http';
import { Response } from '../../auth/response.interface';
import { TwoFactor } from '../../auth/twoFactor.interface';
import { UtilisateurService } from './../../Services/utilisateur.service';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, ValidatorFn, AbstractControl } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PasswordConfirmationValidatorService } from './../../auth/password-confirmation-validator.service';
import { JournalisationService } from '../../Services/journalisation.service';
import { ActionService } from '../../Services/action.service';
import { Action } from '../../Models/action.model';


@Component({
  selector: 'app-two-step-verification',
  templateUrl: './two-step-verification.component.html'
})

export class TwoStepVerificationComponent implements OnInit {
  private provider!: string;
  private email!: string;
  private returnUrl!: string;
  twoStepForm!: FormGroup;
  showError!: boolean;
  showSuccess!: boolean;
  errorMessage!: string;

  constructor(private UtilisateurService: UtilisateurService, private passConfValidator: PasswordConfirmationValidatorService, private router: Router,
              private route: ActivatedRoute, private JournalisationService: JournalisationService,private ActionService: ActionService){}


  ngOnInit(): void {
    this.twoStepForm = new FormGroup({
      twoFactorCode: new FormControl('', [Validators.required]),
    });
    this.provider = this.route.snapshot.queryParams['provider'];
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'];
    this.UtilisateurService.Recherche(this.route.snapshot.queryParams['email']).subscribe((data) => {
      this.email = data[0].email;
    })
  }

  validateControl = (controlName: string) => {
    return this.twoStepForm.get(controlName)!.invalid && this.twoStepForm.get(controlName)!.touched
  }

  hasError = (controlName: string, errorName: string) => {
    return this.twoStepForm.get(controlName)!.hasError(errorName)
  }

  loginUser = (twoStepFormValue : any) => {
    this.showError = false;
    const formValue = { ...twoStepFormValue };
    let twoFactorDto: TwoFactor = {
      email: this.email,
      provider: this.provider,
      token: formValue.twoFactorCode
    }
    console.log(twoFactorDto);
    this.UtilisateurService.twoStepLogin(twoFactorDto).subscribe({
      next: (res: Response) => {
          this.showSuccess = true;
          localStorage.setItem("token", res.token);
          this.UtilisateurService.sendAuthStateChangeNotification(res.isAuthSuccessful);
          this.router.navigate(["/Chargement"]);
        },
      error: (err: HttpErrorResponse) => {
          this.errorMessage = "Verification du code échouée !";
          this.showError = true;
        }
      })
  }




}




<div class="AccueilContent" style="display:flex; justify-content:center; align-items:center;">

  <img src="../../assets/Images/PC-AccueilPrincipale.png" style="width:350px; height:350px" />

  <div style="display:flex; flex-direction:column; justify-content:center; align-items:center; margin:0px 0px 0px -20px;">
    <img src="../../assets/Images/WBConcept-AccueilPrincipale.png" style="width:70%;" />

     <div style="max-width:600px;" *ngIf="showError" class="alert alert-danger centered-element" role="alert"> <span [innerHTML]="errorMessage"></span>  </div>
     <div style="max-width:600px;" *ngIf="showSuccess" class="alert alert-success centered-element" role="alert"> Verification effectuée ! </div>

    <form class="register" [formGroup]="twoStepForm" autocomplete="off" novalidate (ngSubmit)="loginUser(twoStepForm.value)">

      <em *ngIf="validateControl('twoFactorCode') && hasError('twoFactorCode', 'required')">Saisir le code de verification !</em>
      <input class="auth-field" placeholder="CODE" type="text" id="twoFactorCode" formControlName="twoFactorCode" required>

      <button id="forgotPasswordBtn" type="submit" class="auth-btn" [disabled]="!twoStepForm.valid">VALIDER</button>
      <div style="color:#C4BDF6; padding-top: 5px"> •────────────────────────• </div>
      <div class="mdp-oublié" [routerLink]="['/Authentification']">  Revenir à l'écran de connexion </div>

    </form>

  </div>
</div>
