import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UtilisateurService } from '../../../Services/utilisateur.service';
import { Utilisateur } from '../../../Models/utilisateur.model';


@Component({
  selector: 'app-ModifierProfil',
  templateUrl: './ModifierProfil.component.html'
})

export class ModifierProfilComponent implements OnInit {
  Utilisateur: Utilisateur = new Utilisateur();
  Utilisateurs: any[] = [];
  UserUpdate!: {
    userName: string | null;
    nom: string | null;
    prenom: string | null;
    email: string | null;
    status: boolean | null;
  }; 
  choix: number = 1;
  errorMessage: string = '';
  showError: boolean = false;
  oldEmail: string = "";
  oldUserName: string = "";

  constructor(private UtilisateurService: UtilisateurService, private router: Router, private route: ActivatedRoute){}

  ngOnInit(): void {
    const username = localStorage.getItem("username");
    this.UtilisateurService.getUtilisateurs().subscribe((data) => {
      this.Utilisateurs = data;
      this.Utilisateurs = this.Utilisateurs.filter(user => { return user.userName.toLowerCase().includes(username!.toLowerCase()); });
      this.Utilisateur = this.Utilisateurs[0];
    });
  }


  onSubmit() {
    this.showError = false;
    if (this.choix == 1) this.Utilisateur.status = true;
    if (this.choix == 0) this.Utilisateur.status = false;
    this.UserUpdate.userName = this.Utilisateur.userName;
    this.UserUpdate.email = this.Utilisateur.email;
    this.UserUpdate.nom = this.Utilisateur.nom;
    this.UserUpdate.prenom = this.Utilisateur.prenom;
    this.UserUpdate.status = this.Utilisateur.status;
    this.UtilisateurService.getUtilisateurs().subscribe((data) => {
      const users = data.filter((user) => { return user.email.toLowerCase().includes(this.UserUpdate.email!.toLowerCase()); });
      if (users.length != 0 && this.UserUpdate.email != this.oldEmail) {
        this.errorMessage = "Un utilisateur avec l'email " + this.UserUpdate.email + " existe déjà dans l'application !";
        this.showError = true;
      }
      else {
        this.UtilisateurService.updateUtilisateur(this.Utilisateur.id, this.UserUpdate).subscribe((response) => {
          if (response) { this.router.navigate(['/Utilisateur']); }
        });
      }
    });
  }



}


<div class="background-titre">
  <img src="../../assets/Images/WBConcept-AccueilPrincipale.png" style="width:280px; transform:translateY(6px)" />
</div>


<div class="ajouter-content">

  <form (ngSubmit)="onSubmit()" class="yellow-border">
    <div class="form-content">

      <div style="transform:translateX(-50px); max-width:600px;" *ngIf="showError" class="alert alert-danger centered-element" role="alert"> {{errorMessage}}</div>

      <div class="form-content-rows">
        <label for="nom">UTILISATEUR :</label>
        <input class="yellow-border" type="text" id="userName" name="userName" [(ngModel)]="Utilisateur.userName" style="cursor:not-allowed" readonly>
      </div>

      <div class="form-content-rows">
        <label for="nom">EMAIL :</label>
        <input class="yellow-border" type="email" id="email" name="email" [(ngModel)]="Utilisateur.email" required>
      </div>

      <div class="form-content-rows">
        <label for="nom">PRENOM :</label>
        <input class="yellow-border" type="text" id="prenom" name="prenom" [(ngModel)]="Utilisateur.prenom" required>
      </div>

      <div class="form-content-rows">
        <label for="nom">NOM :</label>
        <input class="yellow-border" type="text" id="nom" name="nom" [(ngModel)]="Utilisateur.nom" required>
      </div>

      <div class="form-content-rows">
        <label for="nom">STATUS :</label>
        <select class="yellow-border" id="choix" name="choix" [(ngModel)]="choix">
          <option value="1">Activé</option>
          <option value="0">Desactivé</option>
        </select>
      </div>

      <div class="form-content-rows">
        <label class="no-select"></label> <div> <button type="submit" class="valider">VALIDER</button> </div>
      </div>


    </div>
  </form>


</div>
