import { Component, Inject, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { FonctionService } from '../../../Services/fonction.service';
import { Fonction } from '../../../Models/fonction.model';
import { ApplicationService } from '../../../Services/application.service'; 
import { Application } from '../../../Models/application.model';


@Component({
  selector: 'app-AjouterFonction',
  templateUrl: './AjouterFonction.component.html'
})
export class AjouterFonctionComponent implements OnInit{
  public fonction: Fonction = new Fonction();
  public applications: Application[] = []; 
  public IdApp: number = 0;
  public rech: string = "";

  constructor(private FonctionService: FonctionService, private applicationService: ApplicationService ,private router: Router){}



  ngOnInit(): void {  this.loadApplications()  }


  loadApplications() {
    this.applicationService.getApplications().subscribe((data) => { this.applications = data; },
      (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
  }


  getSelectedApplicationId(): any {
    const selectedApplication = this.applications.find((app) => app.nom === this.rech); 
    if (selectedApplication) return selectedApplication.id; else return 0; 
  }

  onSubmit() {
    this.IdApp = this.getSelectedApplicationId();
    this.fonction.idApplication = this.IdApp;
    this.FonctionService.addFonction(this.fonction, this.IdApp).subscribe((response) => { if (response) { this.router.navigate(['/Fonction']); } });
    console.log(this.fonction);
  }







}
<div class="background-titre"> <h1> GESTION DES FONCTIONS </h1> </div>


<div class="ajouter-content">

  <div class="ajouter-entete">
    <div class="ajouter-return" [routerLink]="['/Fonction']"> <i class="fa-solid fa-angles-left"></i> RETURN </div>
    <div class="ajouter-titre"> AJOUT D'UNE FONCTION </div>
  </div>



  <form (ngSubmit)="onSubmit()">
    <div class="form-content">
      <div class="form-content-rows">
        <label for="nom">APPLICATION :</label>
        <select id="choix" name="choix" [(ngModel)]="rech" (ngModelChange)="getSelectedApplicationId()">
          <option *ngFor="let app of applications" [value]="app.nom">{{ app.nom }}</option>
        </select>
      </div>


      <div class="form-content-rows">
        <label for="nom">TYPE:</label>
        <select id="choix" name="choix" [(ngModel)]="fonction.type">
          <option value="Multifonctions">Multifonctions</option>
          <option value="Fonction Unique">Fonction Unique</option>
        </select>
      </div>

      <div class="form-content-rows">
        <label for="nom">NOM :</label>
        <input type="text" id="nom" name="nom" [(ngModel)]="fonction.nom" required>
      </div>

      <div class="form-content-rows">
        <label for="description">DESCRIPTION :</label>
        <div style="display: flex; flex-direction: column;">
          <textarea id="description" name="description" rows="4" [(ngModel)]="fonction.description" required></textarea>
        </div>
      </div>

      <div class="form-content-rows">
        <label class="no-select"></label> <div> <button type="submit" class="valider">VALIDER</button> </div>
      </div>

    </div>
   </form>


</div>
