import { Component, Inject, ElementRef, ViewChild ,OnInit } from '@angular/core';
import { Utilisateur } from '../../../Models/utilisateur.model';
import { ActivatedRoute, Router } from '@angular/router';
import { UtilisateurService } from '../../../Services/utilisateur.service';
import { JournalisationService } from '../../../Services/journalisation.service';


@Component({
  selector: 'app-Activite',
  templateUrl: './Activite.component.html'
})
export class ActiviteComponent implements OnInit {
  @ViewChild('tab2', { static: false }) tab2: ElementRef | undefined;
  @ViewChild('tab1', { static: false }) tab1: ElementRef | undefined;
  rech: string = "";
  Utilisateur: Utilisateur = new Utilisateur();
  Journalisations: any[] = [];

  constructor(private JournalisationService: JournalisationService ,private UtilisateurService: UtilisateurService, private router: Router, private route: ActivatedRoute){}


  ngOnInit(): void {
      throw new Error('Method not implemented.');
  }

  loadJournalisation() {
    this.JournalisationService.getJournalisations(this.Utilisateur.id).subscribe((data) => {
      this.Journalisations = data;
    });
  }

  loadActions() {

  }

  Recherche() {
    if (this.rech == ""){}
    else {
      this.UtilisateurService.Recherche(this.rech).subscribe((data) => {
        this.Utilisateur = data[0];
        this.loadJournalisation();
      })
    }
  }

  scrollToTab2() { if (this.tab2) { this.tab2.nativeElement.scrollIntoView({ behavior: 'smooth', block: 'start' }); } }
  scrollToTab1() { if (this.tab1) { this.tab1.nativeElement.scrollIntoView({ behavior: 'smooth', block: 'start' }); } }
}



    <div class="background-titre centered-element"> <h1> GESTION DES UTILISATEURS </h1> </div>
    <div class="centered-element">
      <div class="content">

        <div style="display:flex; justify-content:center;">
          <button class="user-button" id="user-button" [routerLink]="['/Utilisateur']" [routerLinkActive]="['user-button-active']" [routerLinkActiveOptions]="{ exact: true }">
            <i class="fa-solid fa-list"></i>  &nbsp; LISTE
          </button>
          <button class="user-button" id="user-button" [routerLink]="['/Utilisateur/Acces']" [routerLinkActive]="['user-button-active']">
            <i class="fa-solid fa-user-lock"></i> &nbsp; ACCÈS
          </button>
          <button class="user-button" id="user-button" [routerLink]="['/Utilisateur/Activite']" [routerLinkActive]="['user-button-active']">
            <i class="fa-solid fa-clock-rotate-left"></i>  &nbsp; ACTIVITÉ
          </button>
        </div>

        <div class="search-bar-and-add" style="margin:0px 25% 0px 25%;">
          <button class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-user" style="color:gold;"></i> </button>
          <input [(ngModel)]="rech" (input)="Recherche()" class="search-input" type="text" placeholder="Saisir un nom d'utilisateur">
          <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
        </div>

        <hr style="color:aqua;" />
        <div class="search-bar-and-add">
          <button (click)="scrollToTab2()" class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-angles-down" style="color:gold;"></i> </button>
          <input class="search-input" type="text" placeholder="Rechercher...">
          <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
        </div>
        <br />

        <div style="text-align: center; border: 1.5px solid #349AB5; border-radius: 4px; padding: 4px; font-weight: 500; padding: 5px 0px 0px 5px;">
          <h4> DATE D'INSCRIPTION : 2023-09-02T14:59:30.8364623+02:00 </h4>
        </div>
        <br />

        <table style="margin-bottom:50px;" aria-labelledby="tableLabel" *ngIf="Journalisations" #tab1>
          <thead>
            <tr>
              <th>APPLICATION</th>
              <th>DATE CONNEXION</th>
              <th>ADRESSE IP</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let j of Journalisations">
              <td>  </td>
              <td> {{ j.dateConnexion }} </td>
              <td> {{ j.adresseIP }} </td>
            </tr>
          </tbody>
        </table>
        <br /><br /><br /><br />


        <!--------------------------------------------------------------------------------------------------------------------------------------------------------->
        <hr style="color:aqua;" />
        <div class="search-bar-and-add">
          <button (click)="scrollToTab1()" class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-angles-up" style="color:gold;"></i> </button>
          <input class="search-input" type="text" placeholder="Rechercher...">
          <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
        </div>
        <br />

        <div *ngFor="let _ of [].constructor(4); let i = index" style="margin-bottom: -40px; border-radius: 4px; padding: 20px; font-weight: 500;">
          <div style="margin-bottom: 15px; border: 1.5px solid #3398B2; padding: 2px; background-color: #274a65;
               border-radius:5px; padding:5px 0px 0px 5px; text-align:center;">
          <h4> DATE DE CONNEXION : 12/09/2023 </h4> </div>
          <table aria-labelledby="tableLabel" *ngIf="" #tab2>
            <thead>
              <tr>
                <th>APPLICATION</th>
                <th>ACTION</th>
                <th>DATE</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="">
                <td> Nom Application </td>
                <td>  </td>
                <td>  </td>
              </tr>
            </tbody>
          </table>
          <br /><br /><br /><br />
        </div>

        </div>
    </div>
