import { Component, NgModule, OnInit, NgZone } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UtilisateurService } from '../../Services/utilisateur.service';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Response } from './../../auth/response.interface';
import { environment } from '../../../environments/environment';



@Component({
  selector: 'app-LinkedInResponse',
  templateUrl: './LinkedInResponse.component.html',
})

export class LinkedInResponseComponent implements OnInit {
  private apiUrl = environment.apiUrl + 'Utilisateur'; 
  private linkedInToken = "";

  constructor(private router: Router, private ngZone: NgZone, private http: HttpClient,private UtilisateurService: UtilisateurService, private route: ActivatedRoute ){}


  ngOnInit(): void {
    this.linkedInToken = this.route.snapshot.queryParams["code"];
    this.UtilisateurService.LoginWithLinkedIn(this.linkedInToken);
    console.log(this.linkedInToken);
  }






}


<div class="AccueilContent" style="display:flex; justify-content:center; align-items:center;">
  <div style="display: flex; justify-content: center; align-items: center; background-color: #0E1F31; width:100%; height:100%">
    <div>
      <img style="width: 100%;" src="../../assets/Images/WBConcept-AccueilPrincipale.png" />
      <br />
      <div class="centered-element" style="cursor:pointer; border:solid 2px white; border-radius:5px; padding:10px; margin:0px 150px 0px 150px" (click)="getCode()">
        TOKEN
      </div>
      <br />
      <div class="centered-element" style="cursor:pointer; border:solid 2px white; border-radius:5px; padding:10px; margin:0px 150px 0px 150px" (click)="getUserInfo()">
        USERINFO
      </div>
    </div>
  </div>
</div>
