import { Component, NgModule, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CommonModule ,Location } from '@angular/common';
import { UtilisateurService } from '../../Services/utilisateur.service';
import { Utilisateur } from '../../Models/utilisateur.model';
import { RouterModule } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { HttpErrorResponse } from '@angular/common/http';


@Component({
  selector: 'app-InscriptionAppExterne',
  templateUrl: './InscriptionAppExterne.component.html',
})

export class InscriptionAppExterneComponent implements OnInit {
  registerForm!: FormGroup;
  errorMessage: string = '';
  showError!: boolean;


  constructor(private authService: UtilisateurService, private router: Router, private location: Location){}

  ngOnInit(): void {
    if (this.authService.isUserAuthenticated()) this.router.navigate(['/Accueil']);
    else if (!this.authService.isUserAuthenticated()) this.authService.clearCookies();

    this.registerForm = new FormGroup({
      Nom: new FormControl('', [Validators.required]),
      Prenom: new FormControl('', [Validators.required]),
      userName: new FormControl('', [Validators.required]),
      Email: new FormControl('', [Validators.required, Validators.email]),
      Password: new FormControl('', [Validators.required, Validators.minLength(6)]),
      Confirm: new FormControl('' , [Validators.required]),
    }); 
  }


  public validateControl = (controlName: string) => {
     return this.registerForm.get(controlName)!.invalid && this.registerForm.get(controlName)!.touched
  }

  public comparePassword = (mdp: string, confirm: string) => {
    return (this.registerForm.get(mdp)!.value != this.registerForm.get(confirm)!.value) && this.registerForm.get(confirm)!.touched
    }
  

  public hasError = (controlName: string, errorName: string) => {
     return this.registerForm.get(controlName)!.hasError(errorName)
  }


  public registerUser = (registerFormValue: any) => {
    const formValues = { ...registerFormValue };
    if (formValues.Nom && formValues.Prenom && formValues.Email) {
      if (formValues.Confirm == formValues.Password) {
        const user: any = {
          nom: formValues.Nom,
          prenom: formValues.Prenom,
          email: formValues.Email,
          Password: formValues.Password,
          userName: formValues.userName,
          dateInscription: new Date(),
          status: true,
        };
        this.authService.Inscription(user).subscribe({
          next: (res: Response) => {
              this.router.navigate(['/Chargement']);  
          },
          error: (err: HttpErrorResponse) => {
              this.errorMessage = err.message;
              this.showError = true;
          }
        });
      }
      else alert('Confirmation de mot de passe incorrecte');
    }
    else alert('Registration failed');
  }




}


<div class="AccueilContent" style="display:flex; justify-content:center; align-items:center;">

  <img src="../../assets/Images/PC-AccueilPrincipale.png" style="width:350px; height:350px" />

  <div style="display:flex; flex-direction:column; justify-content:center; align-items:center; margin:0px 0px 0px -20px;">
    <img src="../../assets/Images/WBConcept-AccueilPrincipale.png" style="width:70%;" />

    <div style="max-width:600px;" *ngIf="showError" class="alert alert-danger centered-element" role="alert"> {{errorMessage}}  </div>

    <form class="register" [formGroup]="registerForm" autocomplete="off" novalidate (ngSubmit)="registerUser(registerForm.value)">
      <em *ngIf="validateControl('Nom') && hasError('Nom', 'required')">Saisir le nom !</em>
      <input class="auth-field" type="text" placeholder="NOM" id="Nom" name="Nom" formControlName="Nom" required>

      <em *ngIf="validateControl('Prenom') && hasError('Prenom', 'required')">Saisir le prenom !</em>
      <input class="auth-field" type="text" placeholder="PRENOM" id="Prenom" name="Prenom" formControlName="Prenom" required>

      <em *ngIf="validateControl('userName') && hasError('userName', 'required')">Saisir le nom d'utilisateur !</em>
      <input class="auth-field" type="text" placeholder="NOM D'UTILISATEUR" id="userName" name="userName" formControlName="userName" required>

      <em *ngIf="validateControl('Email') && hasError('Email', 'email')">Saisir une adresse email valide !</em>
      <em *ngIf="validateControl('Email') && hasError('Email', 'required')">Saisir une adresse email !</em>
      <input class="auth-field" type="email" placeholder="E-MAIL" id="Email" name="Email" formControlName="Email" required>

      <em *ngIf="validateControl('Password') && hasError('Password', 'required')">Saisir un mot de passe !</em>
      <em *ngIf="validateControl('Password') && hasError('Password', 'minlength')">Le mot de passe doit contenir au moins 6 caractères !</em>
      <input class="auth-field" type="password" placeholder="MOT DE PASSE" id="Password" name="Password" formControlName="Password" required>

      <em *ngIf="validateControl('Confirm') && hasError('Password', 'required')">Saisir un mot de passe !</em>
      <em *ngIf="comparePassword('Password', 'Confirm')"> Confirmation de mot de passe incorrect ! </em>
      <input class="auth-field" type="password" placeholder="CONFIRMATION MOT DE PASSE" id="Confirm" name="Confirm" formControlName="Confirm" required>

      <div style="color:#C4BDF6; margin:-10px 0px 5px 0px;"> •────────────────────────• </div>
      <button class="auth-btn" style="margin:5px 0px 14px 0px;" [disabled]="!registerForm.valid"> S'INSCRIRE </button>
      <div class="mdp-oublié" [routerLink]="['/Authentification']"> Déjà inscrit(e) ? &nbsp; <u>S’identifier</u> </div>
    </form>
  </div>

</div>
