import { Component, Inject, ElementRef, ViewChild ,OnInit } from '@angular/core';
import { Utilisateur } from '../../../Models/utilisateur.model';
import { ActivatedRoute, Router } from '@angular/router';
import { UtilisateurService } from '../../../Services/utilisateur.service';
import { JournalisationService } from '../../../Services/journalisation.service';
import { ActionService } from '../../../Services/action.service';
import { HttpErrorResponse } from '@angular/common/http';
import { PermissionService } from '../../../Services/permission.service';
import { RoleService } from '../../../Services/role.service';
import { ApplicationService } from '../../../Services/application.service';


@Component({
  selector: 'app-Activite',
  templateUrl: './Activite.component.html'
})
export class ActiviteComponent implements OnInit {
  @ViewChild('tab2', { static: false }) tab2: ElementRef | undefined;
  @ViewChild('tab1', { static: false }) tab1: ElementRef | undefined;
  rechUtilisateur: string = "";
  rechJournalisation: string = "";
  rechAction: string = "";
  Utilisateur: Utilisateur = new Utilisateur();
  Journalisations: any[] = [];
  JournalisationsActions: any[] = [];
  JournalisationsActionsRech: any[] = [];
  Actions: any[] = [];
  read: boolean = true;

  constructor(private JournalisationService: JournalisationService, private UtilisateurService: UtilisateurService, private ActionService: ActionService,
    private router: Router, private route: ActivatedRoute, private PermissionService: PermissionService, private ApplicationService: ApplicationService,
    private RoleService: RoleService){}


  ngOnInit(): void {
    this.setPermissionsByRole();
  }


  setPermissionsByRole() {
    this.ApplicationService.Recherche("www.wbauth.com").subscribe((dataApp) => {
      if (dataApp[0].url == "www.wbauth.com") {
        let role = localStorage.getItem("role");
        this.RoleService.Recherche(role, dataApp[0].id).subscribe((dataRole) => {
          if (dataRole && dataRole.length == 1) {
            this.PermissionService.getListeFonctionUnique(dataApp[0].id, dataRole[0].id).subscribe((dataPermission) => {
              for (const perm of dataPermission) {
                if (perm.nom == "Consulter la journalisation d'un utilisateur") {
                  if (perm.status === "0") { this.read = false; }
                }
              }
            })
          }
        })
      }
    });
  }

  loadJournalisation(){
    this.JournalisationService.getJournalisations(this.Utilisateur.id).subscribe((data) => {
      this.Journalisations = data;
    });
  }

  loadActions() {
    this.JournalisationService.getJournalisations(this.Utilisateur.id).subscribe((data) => {
      this.JournalisationsActions = data;
      this.JournalisationsActions.forEach((journalisation) => {
        this.ActionService.getActions(journalisation.id).subscribe((dataActions) => {
          journalisation.actions = dataActions;
        });
      });
    });
  }

  RechercheUtilisateur() {
    this.Journalisations = []
    this.Utilisateur.dateInscription = null;
    this.UtilisateurService.getUtilisateurs().subscribe((data) => {
      for (const user of data) {
        if (this.rechUtilisateur === user.userName) {
          this.Utilisateur = user;
          this.loadJournalisation();
          this.loadActions();
        }
      }
    });
  }


  RechercheJournalisation() {
    this.Journalisations = []
    this.JournalisationService.getJournalisations(this.Utilisateur.id).subscribe((data) => {
      this.Journalisations = data;
      this.Journalisations = this.Journalisations.filter(j => {
        return j.dateConnexion.toLowerCase().includes(this.rechJournalisation.toLowerCase()) ||
               j.application.toLowerCase().includes(this.rechJournalisation.toLowerCase());
      });
    });
  }


  RechercheAction(){
    this.Actions = [];
    this.JournalisationsActions = [];
    this.JournalisationService.getJournalisations(this.Utilisateur.id).subscribe((data) => {
      this.JournalisationsActionsRech = data;
      this.JournalisationsActionsRech.forEach((journalisation) => {
        this.ActionService.getActions(journalisation.id).subscribe((dataActions) => {
          journalisation.actions = dataActions.filter(j => {
            return j.date.toLowerCase().includes(this.rechAction.toLowerCase()) ||
                   j.application.toLowerCase().includes(this.rechAction.toLowerCase()) ||
                   j.description.toLowerCase().includes(this.rechAction.toLowerCase());
          });
          this.JournalisationsActions = this.JournalisationsActionsRech.filter(j => { return j.actions && j.actions.length !== 0 });
        });
      });
    });
  }


  scrollToTab2(){ if (this.tab2) { this.tab2.nativeElement.scrollIntoView({ behavior: 'smooth', block: 'start' }); } }
  scrollToTab1(){ if (this.tab1) { this.tab1.nativeElement.scrollIntoView({ behavior: 'smooth', block: 'start' }); } }
}



    <div class="background-titre centered-element"> <h1> GESTION DES UTILISATEURS </h1> </div>
    <div class="centered-element">

      <h4 *ngIf="!read" style="margin-top:100px" class="alert alert-danger" role="alert">
        Accès refusé : Vous n'êtes pas autorisé à consulter la journalisation des utilisateurs !
      </h4>

      <div *ngIf="read" class="content">

        <div style="display:flex; justify-content:center;">
          <button class="user-button" id="user-button" [routerLink]="['/Utilisateur']" [routerLinkActive]="['user-button-active']" [routerLinkActiveOptions]="{ exact: true }">
            <i class="fa-solid fa-list"></i>  &nbsp; LISTE
          </button>
          <button class="user-button" id="user-button" [routerLink]="['/Utilisateur/Acces']" [routerLinkActive]="['user-button-active']">
            <i class="fa-solid fa-user-lock"></i> &nbsp; ACCÈS
          </button>
          <button class="user-button" id="user-button" [routerLink]="['/Utilisateur/Activite']" [routerLinkActive]="['user-button-active']">
            <i class="fa-solid fa-clock-rotate-left"></i>  &nbsp; ACTIVITÉ
          </button>
        </div>

        <div class="search-bar-and-add" style="margin:0px 25% 0px 20%;">
          <button class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-user" style="color:gold;"></i> </button>
          <input [(ngModel)]="rechUtilisateur" (input)="RechercheUtilisateur()" class="search-input" type="text" placeholder="Saisir un nom d'utilisateur..">
          <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
        </div>

        <hr style="color:aqua;" />
        <div class="search-bar-and-add">
          <button (click)="scrollToTab2()" class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-angles-down" style="color:gold;"></i> </button>
          <input [(ngModel)]="rechJournalisation" (input)="RechercheJournalisation()" class="search-input" type="text" placeholder="Saisir une date ou une application...">
          <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
        </div>
        <br />

        <div style="text-align: center; border: 1.5px solid #349AB5; border-radius: 4px; padding: 4px; font-weight: 500; padding: 5px 0px 0px 5px;">
          <h4> DATE D'INSCRIPTION : {{ Utilisateur.dateInscription }} </h4>
        </div>
        <br />

        <table style="margin-bottom:50px;" aria-labelledby="tableLabel" *ngIf="Journalisations" #tab1>
          <thead>
            <tr>
              <th>APPLICATION</th>
              <th>DATE CONNEXION</th>
              <th>ADRESSE IP</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let j of Journalisations">
              <td> {{ j.application }}  </td>
              <td> {{ j.dateConnexion }} </td>
              <td> {{ j.adresseIP }} </td>
            </tr>
          </tbody>
        </table>
        <br /><br />


        <!-------------------------------------------------------------------------------------------------------------------------------------------------------------------------------->
        <hr style="color:aqua;" />
        <div class="search-bar-and-add">
          <button (click)="scrollToTab1()" class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-angles-up" style="color:gold;"></i> </button>
          <input [(ngModel)]="rechAction" (input)="RechercheAction()" class="search-input" type="text" placeholder="Saisir une date ,une application ou une action...">
          <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
        </div>
        <br />

        <div *ngFor="let ja of JournalisationsActions; let i = index" style="margin-bottom: -40px; border-radius: 4px; padding: 20px; font-weight: 500;" #tab1>
          <div style="margin-bottom: 15px; border: 1.5px solid #3398B2; padding: 2px; background-color: #274a65; border-radius:5px; padding:5px 0px 0px 5px; text-align:center;">
            <h4> DATE DE CONNEXION : {{ ja.dateConnexion }} </h4>
          </div>
          <table aria-labelledby="tableLabel" *ngIf="ja.actions && ja.actions.length > 0">
            <thead>
              <tr>
                <th>APPLICATION</th>
                <th>DATE</th>
                <th>ACTION</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let action of ja.actions">
                <td>{{ action.application }}</td>
                <td>{{ action.date }}</td>
                <td style="word-wrap: break-word; overflow-wrap: break-word;">{{ action.description }}</td>
              </tr>
            </tbody>
          </table>
          <br /><br /><br /><br />
        </div>


      </div>
    </div>
