import { Component, Inject, ElementRef, ViewChild } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Role } from '../../Models/role.model';
import { RoleService } from '../../Services/role.service';
import { Application } from '../../Models/application.model';
import { ApplicationService } from '../../Services/application.service';
import { Permission } from '../../Models/permission.model';
import { PermissionService } from '../../Services/permission.service';
import { filter, Observable } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';
import { JournalisationService } from '../../Services/journalisation.service';
import { UtilisateurService } from './../../Services/utilisateur.service';
import { ActionService } from '../../Services/action.service';
import { Action } from '../../Models/action.model';

@Component({
  selector: 'app-Permission',
  templateUrl: './Permission.component.html'
})
export class PermissionComponent {
  @ViewChild('table2', { static: false }) table2: ElementRef | undefined;
  @ViewChild('table1', { static: false }) table1: ElementRef | undefined;
  permission: Permission = new Permission();
  roles: any[] = [];
  applications: any[] = [];
  multiPermissions: any[] = [];
  uniquePermissions: any[] = [];
  rechApp: string = '';
  rechRole: string = '';
  rechFonctionMulti: string = '';
  rechFonctionUnique: string = '';
  IdApp: number = 0;
  IdRole: number = 0;
  i: number = 0;
  listePermissionsPredefini: any[] = [];

  constructor(private PermissionService: PermissionService, private RoleService: RoleService, private route: ActivatedRoute, private router: Router,
    private ApplicationService: ApplicationService, private JournalisationService: JournalisationService, private ActionService: ActionService,
    private UtilisateurService: UtilisateurService){}


  ngOnInit(): void {
    this.ApplicationService.getApplications().subscribe((data) => {
      this.applications = data;
      if (this.applications && this.applications.length > 0) {
        this.IdApp = this.applications[0].id;
        this.rechApp = this.applications[0].nom;
        this.RoleService.getRoles(this.IdApp).subscribe((data) => {
          this.roles = data;
          this.IdRole = this.roles[0].id;
          this.rechRole = this.roles[0].nom;
          this.ajouterListePermissionsPredefini();
          this.loadPermissions();
        })
      }
    },
      (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
  }


  loadApplications(){
    this.ApplicationService.getApplications().subscribe((data) => { this.applications = data; },
      (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
  }


  getSelectedApplicationId(): any {
    const selectedApplication = this.applications.find((app) => app.nom === this.rechApp);
    if (selectedApplication) {
      this.IdApp = selectedApplication.id;
      this.RoleService.getRoles(this.IdApp).subscribe((data) => {
        this.roles = data;
        this.IdRole = this.roles[0].id;
        this.rechRole = this.roles[0].nom;
        this.loadPermissions();
      },
        (error) => { console.error('Erreur lors de la récupération des roles : ', error); });
      return selectedApplication.id;
    } else return 0;
  }

  getSelectedRoleId(): any {
    const selectedRole = this.roles.find((r) => r.nom === this.rechRole);
    if (selectedRole) {
      this.IdRole = selectedRole.id;
      this.loadPermissions();
      return selectedRole.id;
    } else return 0;
  }

  loadPermissions() {
    if (this.applications.length > 0 && this.roles.length > 0){
      this.PermissionService.getListeMultiFonction(this.IdApp, this.IdRole).subscribe((data) => { this.multiPermissions = data; },
        (error) => { console.error('Erreur lors de la récupération des Permissions : ', error); });
      this.PermissionService.getListeFonctionUnique(this.IdApp, this.IdRole).subscribe((data) => { this.uniquePermissions = data; },
        (error) => { console.error('Erreur lors de la récupération des Permissions : ', error); });
    }
  }


  RechMultiFonction(){
    if (this.rechFonctionMulti == "") this.loadPermissions();
    else {
      this.PermissionService.getListeMultiFonction(this.IdApp, this.IdRole).subscribe((data) => {
        this.multiPermissions = data;
        this.multiPermissions = this.multiPermissions.filter(perm => {
          return perm.nom.toLowerCase().includes(this.rechFonctionMulti.toLowerCase());
        });
      });
    }
  }


  RechFonctionUnique() {
    if (this.rechFonctionUnique == "") this.loadPermissions();
    else {
      this.PermissionService.getListeFonctionUnique(this.IdApp, this.IdRole).subscribe((data) => {
        this.uniquePermissions = data;
        this.uniquePermissions = this.uniquePermissions.filter(perm => {
          return perm.nom.toLowerCase().includes(this.rechFonctionUnique.toLowerCase());
        });
      });
    }
  }


  onModifierAccesFonctionMulti(index: number, perm: Permission, event: Event): void {
    this.i = index;
    const target = event.target as HTMLElement;
    const span = target.querySelector('span');
    if (span?.textContent === '✔') { span.textContent = '✖'; target.style.backgroundColor = 'red'; }
    else if (span?.textContent === '✖') { span.textContent = '✔'; target.style.backgroundColor = '#128d3a'; }
    this.PermissionService.ModifierAcces(this.IdApp, this.i, perm).subscribe(() => {
      this.loadPermissions();
      this.EnregistrerAction(this.IdApp, this.IdRole, "MultiFonction" ,perm);
    });
  }


  onModifierAccesFonctionUnique(perm: Permission, event: Event): void {
    const target = event.target as HTMLElement;
    const span = target.querySelector('span');
    if (span?.textContent === '✔' && perm.status == "1") { span.textContent = '✖'; target.style.backgroundColor = 'red'; }
    else if (span?.textContent === '✖' && perm.status == "0") { span.textContent = '✔'; target.style.backgroundColor = '#128d3a';  }
    this.PermissionService.ModifierAcces(this.IdApp, this.i, perm).subscribe(() => {
      this.loadPermissions();
      this.EnregistrerAction(this.IdApp, this.IdRole, "Fonction Unique" ,perm);
    });
  }


  onModifierClick(id: number) { this.router.navigate(['Permission/ModifierPermission', id]); }


  EnregistrerActionSupp(id: number) {
    let username = localStorage.getItem("username");
    this.UtilisateurService.Recherche(username).subscribe((data) => {
      this.JournalisationService.getJournalisations(data[0].id).subscribe((dataJournalisations) => {
        let idJournalisation = dataJournalisations[dataJournalisations.length - 1].id;
        const action: Action = {
          id: 0, application: "www.wbauth.com", date: new Date(), description: `l'utilisateur ${username} a supprimer la permission avec l'id '${id}' du role '${this.rechRole}' dans l'application '${this.rechApp}' avec l'id '${this.IdApp}'`,
          idJournalisation: idJournalisation, journalisation: dataJournalisations[dataJournalisations.length - 1]
        }
        this.ActionService.EnregistrementActions(action).subscribe(() => { console.log(action); });
      });
    });
  }


  deletePermission(id: number) {
    if (confirm('Voulez-vous vraiment supprimer ce Permission ?')) {
      this.PermissionService.deletePermission(id).subscribe(() => {
        this.loadPermissions();
        this.EnregistrerActionSupp(id);
      });
    }
  }


  scrollToTable2() {  if (this.table2) {  this.table2.nativeElement.scrollIntoView({ behavior: 'smooth', block: 'start' }); }}
  scrollToTable1() {  if (this.table1) { this.table1.nativeElement.scrollIntoView({ behavior: 'smooth', block: 'start' }); }}



  EnregistrerAction(IdApp: number ,IdRole: number ,type: string ,perm: Permission){
    let username = localStorage.getItem("username");
    this.UtilisateurService.Recherche(username).subscribe((data) => {
      this.JournalisationService.getJournalisations(data[0].id).subscribe((dataJournalisations) => {
        let idJournalisation = dataJournalisations[dataJournalisations.length - 1].id;
        if (type === "MultiFonction"){
          const action: Action = {
            id: 0, application: "www.wbauth.com", date: new Date(),
            description: `l'utilisateur ${username} a modifier la permission '${perm.nom}' du role avec l'id '${IdRole}' de l'application avec l'id '${IdApp}'  `,
            idJournalisation: idJournalisation, journalisation: dataJournalisations[dataJournalisations.length - 1]
          }
          this.ActionService.EnregistrementActions(action).subscribe(() => { console.log(action); });
        }
        else if (type === "Fonction Unique"){
          const action: Action = {
            id: 0, application: "www.wbauth.com", date: new Date(),
            description: `l'utilisateur ${username} a modifier la permission '${perm.nom}' du role avec l'id '${IdRole}' de l'application avec l'id '${IdApp}'  `,
            idJournalisation: idJournalisation, journalisation: dataJournalisations[dataJournalisations.length - 1]
          }
          this.ActionService.EnregistrementActions(action).subscribe(() => { console.log(action); });
        }
      })
    })
  }


  ajouterListePermissionsPredefini() {
    this.ApplicationService.Recherche("www.wbauth.com").subscribe((dataApp) => {
      if (dataApp[0].url == "www.wbauth.com") {
        this.RoleService.getRoles.subscribe((dataRole) => {
          this.listePermissionsPredefini.push(
            new Permission(0, 'Gestion des applications', 'Multifonction', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Gestion des fonctions', 'Multifonction', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Gestion des roles', 'Multifonction', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Gestion des permissions', 'Multifonction', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Gestion des utilisateurs', 'Multifonction', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Consulter la liste des accès utilisateurs', 'Fonction Unique', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Modifier la liste des accès d\'un utilisateur', 'Fonction Unique', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Consulter la journalisation d\'un utilisateur', 'Fonction Unique', '', this.perm, dataApp[0].id, dataApp[0]),
            new Permission(0, 'Modifier son compte utilisateur', 'Fonction Unique', '', this.perm, dataApp[0].id, dataApp[0]),
          );
          for (const fonction of this.listeFonctionsPredefini) {
            this.FonctionService.Recherche(fonction.nom, dataApp[0].id).subscribe((data) => {
              if (data && data.length < 1) {
                this.FonctionService.addFonction(fonction, dataApp[0].id).subscribe(() => { });
              }
            });
          }
        })
      }
    })
  }



}
      <div class="background-titre"> <h1> GESTION DES PERMISSIONS </h1> </div>
      <div class="centered-element">
        <div class="content">

          <div class="search-bar-and-add">
            <button class="add-button" [routerLink]="['/Permission/AjouterPermission']">
              <i class="fas fa-plus" style="color: #1ddd92;"></i>  &nbsp;  AJOUTER
            </button>
            <input [(ngModel)]="rechFonctionMulti" (input)="RechMultiFonction()" class="search-input" type="text" placeholder="Rechercher...">
            <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
          </div>
          <br />

          <div class="choix-application">
            <label style="transform:translateX(-3px)" for="nom">APPLICATION :</label>
            <select id="IdApp" name="IdApp" [(ngModel)]="rechApp" (ngModelChange)="getSelectedApplicationId()">
              <option *ngFor="let app of applications" [value]="app.nom">{{ app.nom }}</option>
            </select>

            <label style="transform:translateX(-3px)" for="nom">RÔLE :</label>
            <select id="IdRole" name="IdRole" [(ngModel)]="rechRole" (ngModelChange)="getSelectedRoleId()">
              <option *ngFor="let role of roles" [value]="role.nom">{{ role.nom }} ( Niveau {{ role.niveau }} )</option>
            </select>
          </div>


          <div style="white-space:nowrap ; border: 1.5px solid #349AB5; border-radius:4px; padding:4px; font-weight:600;
                 display:flex; align-items:center ">
            <button (click)="scrollToTable2()" class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-angles-down" style="color:gold;"></i> </button>
            <div style="margin:auto;"> TABLEAU MULTI-FONCTIONS </div>
          </div>
          <br />


          <table aria-labelledby="tableLabel" *ngIf="multiPermissions" #table1>
            <thead>
              <tr>
                <th></th>
                <th>LECTURE</th>
                <th>AJOUT</th>
                <th>MODIFICATION</th>
                <th>SUPRESSION</th>
                <th>IMPRESSION</th>
                <th>EXPORTATION</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let permission of multiPermissions">
                <td> <i (click)="onModifierClick(permission.id)" [routerLink]="['/Permission/ModifierPermission']" class="far fa-edit" style="color: #e6c347;"></i>
                     <i (click)="deletePermission(permission.id)" class="far fa-trash-alt" style="color:red; margin:0px 7px 0px 7px;"></i> {{ permission.nom }} </td>

                <td (click)="onModifierAccesFonctionMulti(0,permission ,$event)" style="display:flex; justify-content:center; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status[0] === '0' ? '✖' : '✔' }}">{{ permission.status[0] === '0' ? '✖' : '✔' }}</span>
                </td>

                <td (click)="onModifierAccesFonctionMulti(1,permission ,$event)" style="position:relative; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status[1] === '0' ? '✖' : '✔' }}"
                        style="position:relative; left:45%;">{{ permission.status[1] === '0' ? '✖' : '✔' }}</span>
                </td>

                <td (click)="onModifierAccesFonctionMulti(2,permission ,$event)" style="display:flex; justify-content:center; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status[2] === '0' ? '✖' : '✔' }}">{{ permission.status[2] === '0' ? '✖' : '✔' }}</span>
                </td>

                <td (click)="onModifierAccesFonctionMulti(3,permission ,$event)" style="position:relative; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status[3] === '0' ? '✖' : '✔' }}"
                        style="position: relative; left: 45%;">{{ permission.status[3] === '0' ? '✖' : '✔' }}</span>
                </td>

                <td (click)="onModifierAccesFonctionMulti(4,permission ,$event)" style="display:flex; justify-content:center; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status[4] === '0' ? '✖' : '✔' }}">{{ permission.status[4] === '0' ? '✖' : '✔' }}</span>
                </td>

                <td (click)="onModifierAccesFonctionMulti(5,permission ,$event)" style="position:relative; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status[5] === '0' ? '✖' : '✔' }}"
                        style="position: relative; left: 45%;">{{ permission.status[5] === '0' ? '✖' : '✔' }}</span>
                </td>

              </tr>
            </tbody>


          </table>
          <br /><br /><br /><br />


<!--------------------------------------------------------------------------------------------------------------------------------------------------------->
          <hr style="color:aqua;"/>
          <div class="search-bar-and-add">
            <button class="add-button" [routerLink]="['/Permission/AjouterPermission']">
              <i class="fas fa-plus" style="color: #1ddd92;"></i>  &nbsp;  AJOUTER
            </button>
            <input [(ngModel)]="rechFonctionUnique" (input)="RechFonctionUnique()" class="search-input" type="text" placeholder="Rechercher...">
            <button class="search-button" type="submit"><i class="fas fa-search"></i></button>
          </div>
          <br />

          <div style="white-space:nowrap ; border: 1.5px solid #349AB5; border-radius:4px; padding:4px; font-weight:600;
                 display:flex; align-items:center ">
            <button (click)="scrollToTable1()" class="add-button" style="padding:8.5px;"> <i class="fa-solid fa-angles-up" style="color:gold;"></i> </button>
            <div style="margin:auto;"> TABLEAU DE FONCTION UNIQUE </div>
          </div>
          <br />


          <table aria-labelledby="tableLabel" *ngIf="uniquePermissions" #table2>
            <thead>
              <tr>
                <th></th>
                <th>ACTION</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let permission of uniquePermissions">
                <td>  <i (click)="onModifierClick(permission.id)" [routerLink]="['/Permission/ModifierPermission']" class="far fa-edit" style="color: #e6c347;"></i>
                      <i (click)="deletePermission(permission.id)" class="far fa-trash-alt" style="color:red; margin:0px 7px 0px 7px;"></i> {{ permission.nom }} </td>
                <td (click)="onModifierAccesFonctionUnique(permission ,$event)" style="position:relative; cursor:pointer;">
                  <span class="no-select" appCellStyle="{{ permission.status === '0' ? '✖' : '✔' }}"
                        style="position: relative; left: 45%;">{{ permission.status === '0' ? '✖' : '✔' }}</span>
                </td>
              </tr>
            </tbody>



          </table>
        </div>
      </div>
