import { Component, Inject, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { PermissionService } from '../../../Services/permission.service';
import { Permission } from '../../../Models/permission.model';
import { ApplicationService } from '../../../Services/application.service';
import { Application } from '../../../Models/application.model';
import { Role } from '../../../Models/role.model';
import { Fonction } from '../../../Models/fonction.model';
import { FonctionService } from '../../../Services/fonction.service';
import { RoleService } from '../../../Services/role.service';


@Component({
  selector: 'app-AjouterPermission',
  templateUrl: './AjouterPermission.component.html'
})
export class AjouterPermissionComponent implements OnInit{
   permission: Permission = new Permission();
   applications: Application[] = [];
   application: Application = new Application();
   roles: Role[] = [];
   role: Role = new Role();
   fonctions: Fonction[] = [];
   fonction: Fonction = new Fonction();
   IdApp: number = 0;
   IdFonction: number = 0;
   IdRole: number = 0;
   rechApp: string = "";
   rechFonction: string = "";
   rechRole: string = "";

  constructor(private PermissionService: PermissionService, private fonctionService: FonctionService, private roleService: RoleService,
              private applicationService: ApplicationService, private router: Router){}



  ngOnInit(): void { this.loadApplications() }


  loadApplications() {
    this.applicationService.getApplications().subscribe((data) => { this.applications = data;  },
      (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
  }


  getSelectedApplicationId(): any {
    const selectedApplication = this.applications.find((app) => app.nom === this.rechApp);
    if (selectedApplication) {
      this.IdApp = selectedApplication.id;
      this.fonctionService.getFonctions(this.IdApp).subscribe((data) => { this.fonctions = data; },
        (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
      this.roleService.getRoles(this.IdApp).subscribe((data) => { this.roles = data; },
        (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
      return selectedApplication.id;
    } else return 0;
  }

  
  getSelectedFonctionId(): any {
    const selectedFonction = this.fonctions.find((fn) => fn.nom === this.rechFonction);
    if (selectedFonction) return selectedFonction.id; else return 0;
  }

  getSelectedRoleId(): any {
    const selectedRole = this.roles.find((r) => r.nom === this.rechRole);
    if (selectedRole) return selectedRole.id; else return 0;
  }


  onSubmit() {
    this.permission.idFonction = this.getSelectedFonctionId();
    this.permission.idRole = this.getSelectedRoleId();
    this.PermissionService.addPermission(this.permission).subscribe((response) => { if (response) { this.router.navigate(['/Permission']); } });
    console.log(this.permission);
  }







}
<div class="background-titre"> <h1> GESTION DES PERMISSIONS </h1> </div>


<div class="ajouter-content">

  <div class="ajouter-entete">
    <div class="ajouter-return" [routerLink]="['/Permission']"> <i class="fa-solid fa-angles-left"></i> RETURN </div>
    <div class="ajouter-titre"> AJOUT D'UNE PERMISSION </div>
  </div>



  <form (ngSubmit)="onSubmit()">
    <div class="form-content">

      <div style="transform:translateX(42px)" *ngIf="showError" class="alert alert-danger" role="alert"> {{errorMessage}}</div>

      <div class="form-content-rows">
        <label for="nom">APPLICATION :</label>
        <select id="IdApp" name="IdApp" [(ngModel)]="rechApp" (ngModelChange)="getSelectedApplicationId()">
          <option *ngFor="let app of applications" [value]="app.nom">{{ app.nom }}</option>
        </select>
      </div>


      <div class="form-content-rows">
        <label for="nom">FONCTION:</label>
        <select id="IdFonction" name="IdFonction" [(ngModel)]="rechFonction" (ngModelChange)="getSelectedFonctionId()">
          <option *ngFor="let fonction of fonctions" [value]="fonction.nom">{{ fonction.nom }}</option>
        </select>
      </div>

      <div class="form-content-rows">
        <label for="nom">RÔLE :</label>
        <select id="IdRole" name="IdRole" [(ngModel)]="rechRole" (ngModelChange)="getSelectedRoleId()">
          <option *ngFor="let role of roles" [value]="role.nom">{{ role.nom }} ( Niveau {{ role.niveau }} ) </option>
        </select>
      </div>


      <div class="form-content-rows">
        <label for="nom">NOM :</label>
        <input type="text" id="nom" name="nom" [(ngModel)]="permission.nom" required>
      </div>



      <div class="form-content-rows">
        <label class="no-select"></label> <div> <button type="submit" class="valider">VALIDER</button> </div>
      </div>


    </div>
  </form>


</div>
