import { Component, Inject, OnInit, OnDestroy, ViewChild, ElementRef, Renderer2 } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { ApplicationService } from '../../../Services/application.service';
import { Application } from '../../../Models/application.model';


@Component({
  selector: 'app-AjouterApplication',
  templateUrl: './AjouterApplication.component.html'
})
export class AjouterApplicationComponent implements OnInit, OnDestroy {
  application: Application = new Application();
  auth2FA: boolean | null = null;
  authGoogle: boolean | null = null;
  authFacebook: boolean | null = null;
  authLinkedIn: boolean | null = null;

  constructor(private applicationService: ApplicationService, private router: Router ,private renderer: Renderer2){}



  ngOnInit(): void{
    const hasReloaded = localStorage.getItem('hasReloaded');
    if (!hasReloaded) {
      localStorage.setItem('hasReloaded', 'true');
      location.reload();
    }
  }
  ngOnDestroy(): void {   localStorage.removeItem('hasReloaded');   }


  toggleAuth2FA() {
    const checkDiv = document.querySelector('.check');
    if (checkDiv) {
      const icon = checkDiv.querySelector('i'); 
      if (icon) {
        if (icon.classList.contains('fa-check')) { this.auth2FA = false; }
        else if (icon.classList.contains('fa-times')) { this.auth2FA = true;  }
      }
    }
  }

  toggleAuthGoogle() {
    const checkDiv = document.querySelector('.check2'); 
    if (checkDiv) {
      const icon = checkDiv.querySelector('i'); 
      if (icon) {
        if (icon.classList.contains('fa-check')) { this.authGoogle = false; }
        else if (icon.classList.contains('fa-times')) { this.authGoogle = true;  }
      }
    }
  }

  toggleAuthFacebook() {
    const checkDiv = document.querySelector('.check3'); 
    if (checkDiv) {
      const icon = checkDiv.querySelector('i'); 
      if (icon) {
        if (icon.classList.contains('fa-check')) { this.authFacebook = false; }
        else if (icon.classList.contains('fa-times')) { this.authFacebook = true; }
      }
    }
  }

  toggleAuthLinkedIn() {
    const checkDiv = document.querySelector('.check4'); 
    if (checkDiv) {
      const icon = checkDiv.querySelector('i');
      if (icon) {
        if (icon.classList.contains('fa-check')) { this.authLinkedIn = false; }
        else if (icon.classList.contains('fa-times')) { this.authLinkedIn = true;  }
      }
    }
  }


  onSubmit() {
    if (this.auth2FA == null)  this.application.auth2FA = true;  else this.application.auth2FA = this.auth2FA;
    if (this.authGoogle == null) this.application.authGoogle = true; else this.application.authGoogle = this.authGoogle;
    if (this.authFacebook == null) this.application.authFacebook = true; else this.application.authFacebook = this.authFacebook;
    if (this.authLinkedIn == null) this.application.authLinkedIn = true; else this.application.authLinkedIn = this.authLinkedIn;
    this.applicationService.addApplication(this.application).subscribe((response) => { if (response) { this.router.navigate(['/Application']); } });
    console.log(this.application);
  }


  onFileSelected(event: any){
    if (event.target.files && event.target.files.length > 0){
      const file: File = event.target.files[0];
      const extension: any = file.name.split('.').pop();
      if (file) {
        const allowedExtensions = ["png", "jpg", "jpeg", "gif", "bmp"];
        if (allowedExtensions.indexOf(extension) === -1) {
          alert("Extension de fichier non autorisée. Veuillez sélectionner un fichier avec une extension .png, .jpg, .jpeg, .gif ou .bmp.");
        }
      }
    }
  }






}
<div class="background-titre"> <h1> GESTION DES APPLICATIONS </h1> </div>


<div class="ajouter-content">

  <div class="ajouter-entete">
    <div class="ajouter-return" [routerLink]="['/Application']"> <i class="fa-solid fa-angles-left"></i> RETURN </div>
    <div class="ajouter-titre"> AJOUT D'UNE APPLICATION </div>
  </div>



  <form (ngSubmit)="onSubmit()">
    <div class="form-content">
      <div class="form-content-rows">
        <label style="transform:translateY(-2.5px);" for="nom">NOM :</label>
        <input type="text" id="nom" name="nom" [(ngModel)]="application.nom" required>
      </div>

      <div class="form-content-rows">
        <label style="transform:translateY(-2.5px);" for="nom">URL :</label>
        <input type="text" id="url" name="url" [(ngModel)]="application.url" required>
      </div>

      <div class="form-content-rows">
        <label for="description">DESCRIPTION :</label>
        <div style="display: flex; flex-direction: column;">
          <textarea id="description" name="description" rows="3" [(ngModel)]="application.description" required></textarea>
        </div>
      </div>

      <div class="form-content-rows" style="margin-top:35px;">
        <label for="logo">LOGO :</label>
        <label class="custom-file-input">
          <input type="file" id="logo" name="logo" accept="image/*" [(ngModel)]="application.logo" cursor="pointer" (change)="onFileSelected($event)">
          <div class="custom-file-label"><i class="fa-solid fa-upload"></i> Choisir un fichier</div>
        </label>
      </div>
      <!-- <div id="file-status"></div>  -->

      <div class="form-content-rows">
        <label class="no-select" for="options" style="transform:translateY(-4px)">OPTIONS : </label>
        <div style="margin-top:3px;" class="check" (click)="toggleAuth2FA()" id="auth2fa-check">
          <i class="fa-solid" [ngClass]="{'fa-check': !application.auth2FA, 'fa-times': application.auth2FA}"></i>
        </div>
        <div style="margin-top:3px;" class="auth2fa no-select"> Authentification 2FA </div>
      </div>

      <div class="form-content-rows" style="margin:25px 0px 0px 0px;">
        <label class="no-select"></label>
        <div class="check2" style="margin-right:5px;" (click)="toggleAuthGoogle()">
          <i class="fa-solid" [ngClass]="{'fa-check': !application.authGoogle, 'fa-times': application.authGoogle}"></i>
        </div>
        <img class="no-select" src="../../../../assets/Images/google-icon.png" width="40px" height="40px" style="margin-top:3px;" /> <div class="no-select" style="display:inline;"> &nbsp; &nbsp; </div>

        <div class="check3" style="margin-right:5px;" (click)="toggleAuthFacebook()">
          <i class="fa-solid" [ngClass]="{'fa-check': !application.authFacebook, 'fa-times': application.authFacebook}"></i>
        </div>
        <img class="no-select" src="../../../../assets/Images/facebook-icon.png" width="45px" height="45px" /> <div class="no-select" style="display:inline;"> &nbsp; &nbsp; </div>

        <div class="check4" style="margin-right:5px;" (click)="toggleAuthLinkedIn()">
          <i class="fa-solid" [ngClass]="{'fa-check': !application.authLinkedIn, 'fa-times': application.authLinkedIn}"></i>
        </div>
        <img class="no-select" src="../../../../assets/Images/linkedin-icon.png" width="45px" height="45px" /> <div class="no-select" style="display:inline;"> &nbsp; &nbsp; </div>
      </div>

      <div class="form-content-rows">
        <label class="no-select"></label> <div> <button type="submit" class="valider">VALIDER</button> </div>
      </div>


    </div>
  </form>


</div>
