import { Component, NgModule, OnInit, NgZone } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { UtilisateurService } from '../../Services/utilisateur.service';
import { RoleService } from '../../Services/role.service';
import { Utilisateur } from '../../Models/utilisateur.model';
import { RouterModule } from '@angular/router';
import { HttpErrorResponse } from '@angular/common/http';
import { Login } from './../../auth/login.interface';
import { Response } from './../../auth/response.interface';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { UtilisateurApplicationService } from '../../Services/utilisateurapplication.service';
import { ApplicationService } from '../../Services/application.service';
import { CredentialResponse, PromptMomentNotification } from 'google-one-tap';
import { environment } from '../../../environments/environment';
declare const FB: any;


@Component({
  selector: 'app-Auhtentification',
  templateUrl: './Authentification.component.html',
})

export class AuthentificationComponent implements OnInit {
  private returnUrl!: string;
  private clientId = environment.clientId;
  loginForm!: FormGroup;
  errorMessage: string = '';
  showError!: boolean;
  constructor(private UAService: UtilisateurApplicationService, private RoleService: RoleService, private router: Router ,private ngZone: NgZone
             ,private UtilisateurService: UtilisateurService, private route: ActivatedRoute, private ApplicationService: ApplicationService,){}


  ngOnInit(): void {
    if (localStorage.getItem('username')) this.router.navigate(['/Accueil']);
    this.loginForm = new FormGroup({
      userName: new FormControl("", [Validators.required]),
      password: new FormControl("", [Validators.required])
    })
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';

    // @ts-ignore
    window.onGoogleLibraryLoad = () => {
      // @ts-ignore
      google.accounts.id.initialize({
        client_id: this.clientId,
        callback: this.handleCredentialResponse.bind(this),
        auto_select: false,
        cancel_on_tap_outside: false
      });
      const renderOptions = { type: "icon", shape: "circle", theme: "outline", size: "large", width: "100%" };
      const targetElement = document.getElementById("cnx-google");
      if (targetElement) {
        targetElement.style.opacity = "0";
        targetElement.style.marginBottom = "-40px";
      }
      // @ts-ignore
      google.accounts.id.renderButton(targetElement, renderOptions);
      // @ts-ignore
      google.accounts.id.prompt((notification: PromptMomentNotification) => { });
    }

  }


  async handleCredentialResponse(response: CredentialResponse) {
    console.log(response);
    await this.UtilisateurService.LoginWithGoogle(response.credential).subscribe(
      (x: any) => {  this.ngZone.run(() => { this.router.navigate(['/']); })  },
      (error: any) => { console.log(error);  }
    );
  }



  validateControl = (controlName: string) => {
    return this.loginForm.get(controlName)!.invalid && this.loginForm.get(controlName)!.touched
  }


  hasError = (controlName: string, errorName: string) => {
    return this.loginForm.get(controlName)!.hasError(errorName)
  }


  loginUser = (loginFormValue : any) => {
    this.showError = false;
    const login = { ...loginFormValue };
    const userForAuth: Login = {
      userName: login.userName,
      password: login.password
    }
    this.UtilisateurService.login(userForAuth).subscribe({
      next: (res: Response) => {
          localStorage.setItem("username", login.userName);
          const username = localStorage.getItem("username");
          this.UtilisateurService.Recherche(username).subscribe((userdata) => {
            this.UAService.getListByUtilisateur(userdata[0].id).subscribe((uadata) => {
              this.ApplicationService.Recherche("WBAuth").subscribe((appdata) => {
                let app = uadata.filter((uaApp) => uaApp.idApplication == appdata[0].id);
                if (app[0].acces == false || userdata[0].status == false) {
                  this.showError = true;
                  if (userdata[0].status == false) { this.errorMessage = "Votre compte est désactivé !"; }
                  else if (app[0].acces == false) { this.errorMessage = "Vous n'avez pas l'autorisation d'accéder à cette application !"; }
                }
                else { 
                  if (uadata[0].idRole == null) {
                    localStorage.setItem("role", "ROLE NON EFFECTUÉ");
                    localStorage.setItem("token", res.token);
                    this.UtilisateurService.sendAuthStateChangeNotification(res.isAuthSuccessful);
                    this.router.navigate(['/Chargement']);
                  }
                  else {
                    this.RoleService.getRole(uadata[0].idRole).subscribe((roledata) => {
                      localStorage.setItem("role", roledata.nom);
                      localStorage.setItem("token", res.token);
                      this.UtilisateurService.sendAuthStateChangeNotification(res.isAuthSuccessful);
                      this.router.navigate(['/Chargement']);
                    });
                  }
                }
              });
            });
          });
        },
        error: (err: HttpErrorResponse) => {
          this.errorMessage = err.message;
          this.showError = true;
        }
      })
  }

  cnxGoogle() {
  //  //@ts-ignore
  //  window.onGoogleLibraryLoad = () => {
  //    //@ts-ignore
  //    google.accounts.id.initialize({
  //      client_id: this.clientId,
  //      callback: this.handleCredentialResponse.bind(this),
  //      auto_select: false,
  //      cancel_on_tap_outside: true
  //    }); 
  //    const renderOptions = { type: "icon", shape: "circle", theme: "outline", size: "large", width: "100%" };
  //    const targetElement = document.getElementById("cnx-google");
  //    if (targetElement) {
  //      targetElement.style.opacity = "0";
  //      targetElement.style.marginBottom = "-40px";
  //    }
  //    // @ts-ignore
  //    google.accounts.id.renderButton(targetElement, renderOptions);
  //    // @ts-ignore
  //    google.accounts.id.prompt((notification: PromptMomentNotification) => { });
  // // };
  }


  async cnxFacebook() {
    FB.login(async (result: any) => {
      await this.UtilisateurService.LoginWithFacebook(result.authResponse.accessToken).subscribe(
        (x: any) => {  this.ngZone.run(() => { this.router.navigate(['/']); }) },
        (error: any) => {  console.log(error);  } );}, { scope: 'email' });
  }



}


<div class="AccueilContent" style="display:flex; justify-content:center; align-items:center;">

  <img class="no-select" src="../../assets/Images/PC-AccueilPrincipale.png" style="width:350px; height:350px" />

  <div style="display:flex; flex-direction:column; justify-content:center; align-items:center; margin:0px 0px 0px -20px;">
    <img class="no-select" src="../../assets/Images/WBConcept-AccueilPrincipale.png" style="width:70%;" />

    <div style="max-width:600px;" *ngIf="showError" class="alert alert-danger centered-element" role="alert"> {{errorMessage}}  </div>

    <form class="register" [formGroup]="loginForm" autocomplete="off" novalidate (ngSubmit)="loginUser(loginForm.value)">

      <em *ngIf="validateControl('userName') && hasError('userName', 'required')">Saisir le nom d'utilisateur !</em>
      <input class="auth-field" type="text" placeholder="NOM D'UTILISATEUR" id="userName" name="userName" formControlName="userName" required>
      <em *ngIf="validateControl('password') && hasError('password', 'required')">Saisir le mot de passe !</em>
      <input class="auth-field" type="password" placeholder="MOT DE PASSE" id="mdp" name="mdp" formControlName="password" required>

      <button type="submit" class="auth-btn" [disabled]="!loginForm.valid">SE CONNECTER</button>
      <div class="mdp-oublié" style="transform:translateY(-4px)" [routerLink]="['/ForgotPassword']">  Mot de passe oublié ? </div>

      <div style="margin-top:7px" class="centered-element">
        <div class="rs-signin">
          <div id="cnx-google" (click)="cnxGoogle()"> </div>
          <img src="../../../../assets/Images/google-icon.png" width="40px" height="40px" style="box-shadow:rgb(255,61,0,0.4) 7px 3px; background-color: #F0F8FF; border-radius: 100%; padding: 5px; cursor: pointer; " />
        </div>
        <div style="width:10%;"></div>
        <div class="rs-signin">
          <div class="centered-element" (click)="cnxFacebook()" style="background-color: #F0F8FF; box-shadow:rgb(58,87,148,0.7) 7px 3px; border-radius: 100%; cursor: pointer; width: 40px; height: 40px; ">
            <i class="fa-brands fa-facebook-f fa-xl " style="color: #3A5794;"></i>
          </div>
        </div>
        <div style="width:10%;"></div>
        <div class="rs-signin">
          <div class="centered-element" style="background-color: #F0F8FF; box-shadow:rgb(10,102,194,0.7) 7px 3px; border-radius: 100%; cursor: pointer; width: 40px; height: 40px; ">
             <i class="fa-brands fa-linkedin-in fa-lg" style="color: #0a66c2;"></i>
          </div>
        </div>
      </div>

      <div style="color:#C4BDF6; margin-bottom: 10px;"> •─────────────────────────• </div>
      <div class="auth-inscri-btn" [routerLink]="['/Inscription']"> CRÉER NOUVEAU COMPTE </div>
    </form>


  </div>

</div>
