import { Component, Inject, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { RoleService } from '../../../Services/role.service';
import { Role } from '../../../Models/role.model';
import { ApplicationService } from '../../../Services/application.service';
import { Application } from '../../../Models/application.model';


@Component({
  selector: 'app-AjouterRole',
  templateUrl: './AjouterRole.component.html'
})
export class AjouterRoleComponent implements OnInit{
  public Role: Role = new Role();
  public applications: Application[] = [];
  public IdApp: number = 0;
  public rech: string = "";
  errorMessage: string = '';
  showError: boolean = false;
  constructor(private RoleService: RoleService, private applicationService: ApplicationService, private router: Router){}



  ngOnInit(): void { this.loadApplications(); this.Role.niveau = 1; }


  loadApplications() {
    this.applicationService.getApplications().subscribe((data) => { this.applications = data; },
      (error) => { console.error('Erreur lors de la récupération des applications : ', error); });
  }


  getSelectedApplicationId(): any {
    const selectedApplication = this.applications.find((app) => app.nom === this.rech);
    console.log(selectedApplication);
    if (selectedApplication) return selectedApplication.id; else return 0;
  }


  onSubmit() {
    this.IdApp = this.getSelectedApplicationId();
    this.Role.idApplication = this.IdApp;
    this.RoleService.Recherche(this.Role.nom, this.IdApp).subscribe((data) => {
      if (data.length != 0) {
        this.errorMessage = "Un role avec ce nom existe déjà dans l'application choisit !";
        this.showError = true;
      }
      else {
        this.showError = false;
        this.RoleService.addRole(this.Role).subscribe((response) => { if (response) { this.router.navigate(['/Role']); } });
        console.log(this.Role);
      }
    });
  }







}
<div class="background-titre"> <h1> GESTION DES RÔLES </h1> </div>


<div class="ajouter-content">

  <div class="ajouter-entete">
    <div class="ajouter-return" [routerLink]="['/Role']"> <i class="fa-solid fa-angles-left"></i> RETURN </div>
    <div class="ajouter-titre"> AJOUT D'UN RÔLE </div>
  </div>



  <form (ngSubmit)="onSubmit()">
    <div class="form-content">

      <div style="transform:translateX(42px)" *ngIf="showError" class="alert alert-danger" role="alert"> {{errorMessage}}</div>

      <div class="form-content-rows">
        <label for="nomApp">APPLICATION :</label>
        <select id="choix" name="choix" [(ngModel)]="rech" (ngModelChange)="getSelectedApplicationId()">
          <option *ngFor="let app of applications" [value]="app.nom">{{ app.nom }}</option>
        </select>
      </div>

      <div class="form-content-rows">
        <label for="nom">RÔLE :</label>
        <input type="text" id="nom" name="nom" [(ngModel)]="Role.nom" required>
      </div>

      <div class="form-content-rows">
        <label for="niveau">NIVEAU :</label>
        <input type="number" value="1" id="niveau" name="niveau" [(ngModel)]="Role.niveau" required>
      </div>

      <div class="form-content-rows">
        <label for="niveau">DESCRIPTION :</label>
        <input type="text" id="description" name="description" [(ngModel)]="Role.description" required>
      </div>


      <div class="form-content-rows">
        <label class="no-select"></label> <div> <button type="submit" class="valider">VALIDER</button> </div>
      </div>


    </div>
  </form>


</div>
